import React, { useEffect, useState } from 'react'
import Layout from '../components/Layout/Layout'
import toast from 'react-hot-toast'
import axios from 'axios'
import { NavLink, Navigate } from 'react-router-dom'

import { useNavigate } from 'react-router-dom'

const OngoingEvents = () => {

  const navigate = useNavigate()
  const [events,setEvents] = useState([]);
  const [organiser,setOrganiser] = useState([]);
  const getAllEvents = async() => {
    try{

      const {data} = await axios.get("http://localhost:8080/api/v1/event/get-events")
      if(data?.success)
      {
        toast.success('events were fetched')
        setOrganiser(data?.organiser);
        setEvents(data?.events)
      }
      else
      {
        toast.error(data?.message)
      }

    }
    catch(error)
    {
      console.log(error)
      toast.error("something went wrong")
    }
  }
  useEffect(()=>{
    getAllEvents();
    return ()=>{}
  },[])
  return (
    <Layout>
        <h1>OngoingEvents</h1>
        {
          events.map((ev) => {
            return (
              <>
              <div className="card" style={{width: '18rem'}}> 
                <div className="card-body">
                  <h5 className="card-title">{ev.title}</h5>
                  <p className="card-text">{ev.description}</p>
                  <button className='btn btn-primary ms-2' onClick={()=>navigate(`/event-details/${ev.title}`)}>SEE DETAILS</button>
                </div>
              </div>
              </>
            )
          })
        }
    </Layout>
  )
}

export default OngoingEvents
