import React, { useEffect, useState } from "react";
import Layout from "../components/Layout/Layout";
import { useAuth } from "../Context/auth";

import axios from "axios";

const YourParticipations = () => {
  const [events, setEvents] = useState([]);
  const [auth, setAuth] = useAuth();
  console.log(auth?.user);
  const getPEvents = async () => {
    try {
      console.log(localStorage.getItem("auth"));
      const { data } = await axios.get(
        "http://localhost:8080/api/v1/auth/your-participations",
        {
          headers: {
            Authorization: auth?.token,
          },
        }
      );
      if (data?.success) {
        setEvents(data?.events);
        console.log(events);
      }
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getPEvents();
  }, []);

  const removeHandler = async () => {
    const {data} = await axios.delete("http://localhost:8080/api/v1/event/remove-event")
  }

  return (
    <>
      <Layout>
        <h1>Your participations</h1>
        {events?.map((ev) => {
          return (
            <>
              <div className="yp">
                <div className="card" style={{ width: "18rem" }}>
                  <div className="card-body">
                    <h5 className="card-title">{ev.title}</h5>
                    <p className="card-text">{ev.description}</p>
                    <p className="card-text">{ev.place}</p>
                    <p className="card-text">{ev.date}</p>
                  </div>
                  <button className="btn btn-primary" onClick={removeHandler}>Remove</button>
                </div>
              </div>
            </>
          );
        })}
      </Layout>
    </>
  );
};

export default YourParticipations;
