import React, { useEffect, useState } from 'react'
import Layout from '../components/Layout/Layout'
// import toast from 'react-hot-toast'
import axios from 'axios'
import { NavLink, useParams } from 'react-router-dom'

import { useAuth } from '../Context/auth'
import  toast  from 'react-hot-toast';
import { useNavigate } from 'react-router-dom'


const EventDetails = () => {
    const navigate = useN
  const params = useParams()
  const [auth,setAuth] = useAuth()
  const [event,setEvent] = useState({
    title:"",
    description:"",
    place:"",
    date:"",
    organiser:null
  })
  useEffect(  ()=>{
    if(params?.title) getEvent();

   },[params?.title])

  const getEvent = async() => {
    try{

      const {data} = await axios.get(`http://localhost:8080/api/v1/event/getOneEvent/${params.title}`)
      if(data?.success)
      {
         toast.success('events were fetched')
        setEvent(data?.event)
        console.log(event)
      }
      else
      {
       toast.error(data?.message)
      }

    }
    catch(error)
    {
      console.log(error)
      toast.error("something went wrong")
    }
  }

  const participate = async() =>{
    try{


      const dat = localStorage.getItem('auth')

        const {data} = await axios.put(`http://localhost:8080/api/v1/auth/participate/${params.eid}`)

        if(data?.success)
        {
          toast.success("successfully registered for the event")
          navigate('/yourparticipations')
        }
        else
        {
          toast.error(data?.message)
        }
    }
    catch(error)
    {
      console.log(error)
      toast.error("something went wrong")
    }
  }

  return (
    <Layout>
        <h1>EventDetails</h1>
        <h3>{event?.title}</h3>
        <p>Description:{event?.description}</p>
        <p>Address:{event?.place}</p>
        <p>Date:{event?.date}</p>
        <p>Want to participate in the above event? Click below</p>
        <button className='btn btn-primary' onClick={() => participate()}>Participate</button>
    </Layout>
  )
}

export default EventDetails
