import React, { useEffect, useState } from "react";
import Layout from "../components/Layout/Layout";
import toast from "react-hot-toast";
import axios from "axios";
import { NavLink, Navigate } from "react-router-dom";
import { useNavigate } from "react-router-dom";

const OngoingEvents = () => {
  const navigate = useNavigate();
  const [events, setEvents] = useState([
    {
      title: "",
      description: "",
      place: "",
      date: "",
      organiser: null,
    },
  ]);

  useEffect(() => {
    getAllEvents();
  }, []);

  const getAllEvents = async () => {
    try {
      const { data } = await axios.get(
        "http://localhost:8080/api/v1/event/get-events"
      );
      if (data?.success) {
        toast.success("Events were fetched");
        setEvents(data?.events);
      } else {
        toast.error(data?.message);
      }
    } catch (error) {
      console.log(error);
      toast.error("Something went wrong");
    }
  };

  return (
    <Layout>
      <h1>OngoingEvents</h1>
      <div className="row justify-content-start">
        {events.map((ev) => (
          <div className="col-md-3 col-12 mx-2 my-2" key={ev._id}>
            <div className="card" style={{ width: "25rem" }}>
              <div className="card-body">
                <h5 className="card-title">{ev.title}</h5>
                <p className="card-text">{ev.description}</p>
                <button
                  className="btn btn-primary ms-2"
                  onClick={() =>
                    navigate(`/event-details/${ev.title}/${ev._id}`)
                  }
                >
                  SEE DETAILS
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </Layout>
  );
};

export default OngoingEvents;
