import React, { useState } from 'react';
import Layout from '../components/Layout/Layout';
import axios from 'axios';
import { toast } from 'react-hot-toast';
import { useAuth } from '../Context/auth.js';

const HostAnEvent = () => {
  const { user } = useAuth(); // Assuming user information is available in the authentication context

  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [place, setPlace] = useState('');
  const [date, setDate] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const eventData = {
        title,
        description,
        place,
        date,
        organizer: user._id, // Store the user's ID in the event data
      };

      const { data } = await axios.post('http://localhost:8080/api/v1/event/create-event', eventData);

      if (data.success) {
        toast.success('Created an Event');
      }
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <Layout>
      <div className="container">
        <div className="box3">
          <h1 className="text-center">Host An Event</h1>
          <form>
            <div className="mb-3">
              <input
                type="text"
                className="form-control"
                id="exampleInputTitle1"
                value={title}
                placeholder="Enter title of the event"
                onChange={(e) => {
                  setTitle(e.target.value);
                }}
              />
            </div>
            <div className="mb-3">
              <input
                type="text"
                className="form-control"
                id="exampleInputDescription1"
                value={description}
                placeholder="Enter description of the event"
                onChange={(e) => {
                  setDescription(e.target.value);
                }}
              />
            </div>
            <div className="mb-3">
              <input
                type="text"
                className="form-control"
                id="exampleInputPlace1"
                value={place}
                placeholder="Enter place of the event"
                onChange={(e) => {
                  setPlace(e.target.value);
                }}
              />
            </div>
            <div className="mb-3">
              <input
                type="text"
                className="form-control"
                id="exampleInputDate1"
                value={date}
                placeholder="Enter date of the event"
                onChange={(e) => {
                  setDate(e.target.value);
                }}
              />
            </div>
            <button type="submit" className="btn btn-primary col-md-3" onClick={handleSubmit}>
              Submit
            </button>
          </form>
        </div>
      </div>
    </Layout>
  );
};

export default HostAnEvent;
