import React,{useState} from 'react'
import Layout from '../components/Layout/Layout'
import axios from 'axios';

const SignUpPage = () => {
  const [name,setName] = useState('');
  const [email,setEmail] = useState('');
  const [password,setPassword] = useState('');
  const [phone,setPhone] = useState('');

  const updateName = (e) => {
    setName(e);
  }

  const updateEmail = (e) => {
    setEmail(e);
  }

  const updatePassword= (e) => {
    setPassword(e);
  }

  const updatePhone = (e) => {
    setPhone(e);
  }
  console.log(name,email,password,phone);
  const handleSubmit = async (e) => { 
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:8080/api/v1/auth/register', { name, email, password, phone })
      if (res.data.success) {
        res.send({
          success: true,
          message: "User registered successfully"
        })
        alert('User registered successfully');
      }
      else{
        alert('Failure while registering');
      }
    }
    catch (error) {
      console.log(error)
    }
  }

  return (
    <Layout>
      <div className="container">
        <div className="box2">
          <form>
            <h2>SignUp</h2>
            <div className="mb-3">
              <input type="text" className="form-control" id="exampleInputName" aria-describedby="nameHelp" placeholder="Name" onChange={updateName}/>
            </div>
            <div className="mb-3">
              <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Email" onChange={updateEmail}/>
            </div>
            <div className="mb-3">
              <input type="password" className="form-control" id="exampleInputPassword1" placeholder="Set Password" onChange={updatePassword}/>
            </div>
            <div className="mb-3">
              <input type="text" className="form-control" id="exampleInputPhno" aria-describedby="PHnoHelp" placeholder="Phone Number" onChange={updatePhone}/>
            </div>
            <button type="submit" id="submit" className="btn btn-primary my-3" onClick={handleSubmit}>Submit</button>
          </form>
        </div>
      </div>
    </Layout>
  )
}

export default SignUpPage
