import React, { useEffect, useState } from 'react'
import Layout from '../components/Layout/Layout'
// import toast from 'react-hot-toast'
import axios from 'axios'
import { NavLink, useParams } from 'react-router-dom'

import { useAuth } from '../Context/auth.js'
import  toast  from 'react-hot-toast'
import { useNavigate } from 'react-router-dom'

const EventDetails = () => {

  const params = useParams()
  const navigate = useNavigate()
  const [auth,setAuth] = useAuth()
  const [event,setEvent] = useState({
    title:"",
    description:"",
    place:"",
    date:"",
    organiser:null
  })
  useEffect(  ()=>{
    if(params?.title) getEvent();

   },[params?.title])

  const getEvent = async() => {
    try{

      const {data} = await axios.get(`http://localhost:8080/api/v1/event/getOneEvent/${params.title}`)
      if(data?.success)
      {
         toast.success('events were fetched')
        setEvent(data?.event)
        console.log(event)
      }
      else
      {
       toast.error(data?.message)
      }

    }
    catch(error)
    {
      console.log(error)
      toast.error("something went wrong")
    }
  }

  const participate = async() =>{
    try{
      console.log(auth?.token)
      console.log('/n')
      const dat = localStorage.getItem('auth')
      console.log(auth.token)
        const {data} = await axios.put(`http://localhost:8080/api/v1/auth/participate/${params.eid}`)

        if(data?.success)
        {
          toast.success(data?.message)
         // navigate('/yourparticipations');
          console.log(data?.user?.events)
        }
    }
    catch(error)
    {
      console.log(error)
      toast.error("something went wrong")
    }
  }

  return (
    <Layout>
      <div className="container" id="eventdetails">
        <h3 id="Event">{event?.title}</h3>
        <p><strong>Description</strong>:{event?.description}</p>
        <p><strong>Address:</strong>{event?.place}</p>
        <p><strong>Date:</strong>{event?.date}</p>
        <p>Want to participate in the above event? Click below</p>
        <button className='btn btn-primary' onClick={() => participate()}>Participate</button>
        </div>
    </Layout>
  )
}

export default EventDetails
